const crypto = require('crypto');

class HmacGenerator {
  constructor(secretKey) {
    this.secretKey = secretKey;
  }
  
  generateHmac(value) {
    return crypto.createHmac('sha3-256', this.secretKey)
                .update(value.toString())
                .digest('hex')
                .toUpperCase();
  }
}

module.exports = HmacGenerator;

